<?phpnamespace App\AdminBundle\Business\Account;class Constants{    const ACCOUNT_TYPE_CURRENT = 1;    const ACCOUNT_TYPE_SAVING = 2;    const ACCOUNT_TYPE_CREDIT_CARD = 3;    const ACCOUNT_TYPE_LOAN = 4;    const ACCOUNT_TYPE_OTHER = 5;    public static function getAccountTypes()    {        return array(            self::ACCOUNT_TYPE_CURRENT     => 'Current',            self::ACCOUNT_TYPE_SAVING      => 'Saving',            self::ACCOUNT_TYPE_CREDIT_CARD => 'Credit card',            self::ACCOUNT_TYPE_LOAN        => 'Loan',            self::ACCOUNT_TYPE_OTHER       => 'Other',        );    }    const ACCOUNT_DIRECTION_IN = 1;    const ACCOUNT_DIRECTION_OUT = 2;    public static function getAccountDirections()    {        return array(            self::ACCOUNT_DIRECTION_OUT => 'Out',            self::ACCOUNT_DIRECTION_IN  => 'In',        );    }}