<?phpnamespace App\AdminBundle\Business\Account;use Symfony\Component\Validator\ExecutionContext;class CreateModel{    public $container;    public $name;    public $number;    public $sortCode;    public $idAccountType;    public $street1;    public $street2;    public $county;    public $town;    public $postcode;    public $idCountry;    public $telephone;    public $email;    public function isValid(ExecutionContext $context)    {        $helperDoctrine = $this->container->get('app_admin.helper.doctrine');        if ($helperDoctrine->isNotExist('AppClientBundle:Account', 'email', $this->email) === false) {            $context->addViolationAtSubPath('email', "Email '" . $this->email . "' already exists");        }    }}