<?phpnamespace App\AdminBundle\Business\Account;use App\AdminBundle\Business\Base\BaseGridHandler;class GridHandler extends BaseGridHandler{    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $query->select($baseObject)            ->from('AppClientBundle:Account', $baseObject);        // Build filter here - Consult arrayToSQL($filter, false)        $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }    public function postParseRow(&$r)    {        $r['idAccountType'] = $this->helperMapping->getMappingTitle('account_type', $r['idAccountType']);        $r['balance'] = $this->helperFormatter->format($r['balance'], 'money');    }}