<?phpnamespace App\AdminBundle\Business\Account;use Symfony\Component\Validator\ExecutionContext;class TransferModel{    public $idAccountOut;    public $idAccountIn;    public $amount;    public function isValid(ExecutionContext $context)    {        if ($this->idAccountIn == $this->idAccountOut) {            $context->addViolationAtSubPath('idAccountIn', "Account in should differ from account out");        }    }}