<?phpnamespace App\AdminBundle\Business\AccountTransaction;use App\AdminBundle\Business\Base\BaseGridHandler;class GridHandler extends BaseGridHandler{    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $idAccount = $this->container->get('request')->query->get('id', 0);        $query->select($baseObject)            ->from('AppClientBundle:AccountTransaction', $baseObject)            ->andWhere($baseObject . '.idAccount = :idAccount')            ->setParameter('idAccount', $idAccount);        // Build filter here - Consult arrayToSQL($filter, false)        $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }    public function postParseRow(&$r)    {        $r['timestamp'] = $this->helperFormatter->format($r['timestamp'], 'datetime');    }}