<?phpnamespace App\AdminBundle\Business\AutomationGroup;use App\AdminBundle\Business\Base\BaseGridHandler;class GridHandler extends BaseGridHandler{    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $query->select($baseObject)            ->from('AppClientBundle:AutomationGroup', $baseObject);        // Build filter here - Consult arrayToSQL($filter, false)        $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }    public function postParseRow(&$r)    {        // Process fields        $fields = $this->container->get('app_admin.helper.doctrine')->loadList('AppClientBundle:AutomationGroupField', 'idAutomationGroup', 'idProductField', $r['id']);        $helperMapping = $this->container->get('app_admin.helper.mapping');        $r['fields'] = array();        foreach ($fields as $idProductField) {            $r['fields'][] = $helperMapping->getMappingTitle('automation_group_field', $idProductField);        }        $r['fields'] = implode(', ', $r['fields']);    }}