<?phpnamespace App\AdminBundle\Business\Base;abstract class BaseDeleteHandler{    protected $container;    protected $helperDoctrine;    public function __construct($container)    {        $this->container = $container;        $this->helperDoctrine = $container->get('app_admin.helper.doctrine');    }    abstract public function execute();    public function getModel()    {        $model = $this->container->get('request')->query->get('models', false);        if ($model === false) {            $model = $this->container->get('request')->request->get('models', false);        }        if ($model) {            $model = $model[0];            return $model;        }        return false;    }}