<?phpnamespace App\AdminBundle\Business\Base;abstract class BaseInlineFormHandler extends BaseCreateHandler{    function buildForm($builder)    {        return $builder;    }    public function onSuccess()    {    }    public function onFailure()    {    }    public function execute()    {        $request = $this->container->get('request');        $model = $this->getDefaultValues();        $this->container->get('app_admin.helper.common')->copyArrayToModel($request->request->all(), $model);        $this->model = $model;        if ($this->container->get('validator')->validate($model)) {            $this->onSuccess();        } else {            $this->onFailure();        }    }}