<?phpnamespace App\AdminBundle\Business\ClientContact;class Utils{    public static function getContactEmailsByType($container, $idClient, $type)    {        // TO DO: Filter by type        $query = $container->get('doctrine')->getEntityManager()->createQueryBuilder();        $query->select('p.email')            ->from('AppClientBundle:ClientContact', 'p')            ->andWhere('p.idClient = :idClient')            ->setParameter('idClient', $idClient)        ;        return $query->getQuery()->getResult();    }}