<?phpnamespace App\AdminBundle\Business\ClientNote;use App\AdminBundle\Business\Base\BaseGridHandler;class GridHandler extends BaseGridHandler{    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $idClient = $this->container->get('request')->query->get('id', 0);        $query->select($baseObject)            ->from('AppClientBundle:ClientNote', $baseObject)            ->andWhere($baseObject . '.idClient = :idClient')            ->setParameter('idClient', $idClient);        // Build filter here - Consult arrayToSQL($filter, false)        // $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }    public function postBuilderQuery($query)    {        $query->orderBy('p.timestamp', 'ASC');    }    public function postParseRow(&$r)    {        $r['body'] = html_entity_decode(stripslashes($r["body"]));        $r['body'] = str_replace("&nbsp;", " ", $r["body"]);        $r['body'] = nl2br($r['body']);        $r['timestamp'] = $this->helperFormatter->format($r['timestamp'], 'datetime');    }}