<?phpnamespace App\AdminBundle\Business\Log;use App\AdminBundle\Business\Base\BaseGridHandler;class GridHandler extends BaseGridHandler{    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $query->select($baseObject)            ->from('AppClientBundle:Log', $baseObject)        ;        // Build filter here - Consult arrayToSQL($filter, false)        $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }    public function postParseRow(&$r)    {        $r['timestamp'] = $this->helperFormatter->format($r['timestamp'], 'datetime');        $r['idType'] = $this->helperMapping->getMappingTitle('log_type', $r['idType']);    }    public function getEntityManager()    {        return $this->container->get('doctrine')->getManager('control');    }}