<?phpnamespace App\AdminBundle\Business\Login;use Symfony\Component\Security\Core\Encoder\PasswordEncoderInterface;class BcryptPasswordEncoder implements PasswordEncoderInterface{    public $container;    public function __construct($container)    {        $this->container = $container;    }    public function encodePassword($raw, $salt)    {        $bcrypt = $this->container->get('app_admin.helper.bcrypt');        return $bcrypt->hash($raw);    }    public function isPasswordValid($encoded, $raw, $salt)    {        $bcrypt = $this->container->get('app_admin.helper.bcrypt');        return $bcrypt->verify($raw, $encoded);    }}