<?phpnamespace App\AdminBundle\Business\Login;use Symfony\Component\Security\Core\User\UserInterface;class UserSession implements UserInterface{    private $staff;    private $role;    public function setRole($role)    {        $this->role = $role;    }    public function setStaff($staff)    {        $this->staff = $staff;    }    public function getStaff()    {        return $this->staff;    }    function getPassword()    {        return $this->staff->getPassword();    }    function getSalt()    {        return 'abc';    }    function getUsername()    {        return $this->staff->getEmail();    }    function getRoles()    {        return array($this->role);    }    function eraseCredentials()    {    }    function equals(UserInterface $user)    {        return $this->getEmail() === $user->getEmail();    }}