<?phpnamespace App\AdminBundle\Business\Order;class Constants{    // Order status constants    const ORDER_STATUS_OPEN       = 1;    const ORDER_STATUS_PROCESSING = 2;    const ORDER_STATUS_PAID       = 3;    const ORDER_STATUS_CANCELED   = 4;    const ORDER_STATUS_FRAUDULENT = 5;    const ORDER_STATUS_ACCEPTED   = 5;    const ORDER_STATUS_FAILED     = 6;    const ORDER_STATUS_ACTIVE     = 7;    // Schedule constants    const SCHEDULE_MONTHLY       = 1;    const SCHEDULE_QUARTERLY     = 2;    const SCHEDULE_SEMI_ANNUALLY = 3;    const SCHEDULE_ANNUALLY      = 4;    const SCHEDULE_BIENNIALLY    = 5;    const SCHEDULE_TRIENNIALLY   = 6;    public static function getOrderStatus()    {        return array(            self::ORDER_STATUS_ACTIVE     => 'Active',            self::ORDER_STATUS_PROCESSING => 'Processing',            self::ORDER_STATUS_CANCELED   => 'Canceled',            self::ORDER_STATUS_FRAUDULENT => 'Fraudulent',            self::ORDER_STATUS_FAILED     => 'Failed'        );    }    public static function getOrderPaymentTerms()    {        return array(            self::SCHEDULE_MONTHLY       => 'Monthly',            self::SCHEDULE_QUARTERLY     => 'Quarterly',            self::SCHEDULE_SEMI_ANNUALLY => 'Semi-annually',            self::SCHEDULE_ANNUALLY      => 'Annually',            self::SCHEDULE_BIENNIALLY    => 'Biennially',            self::SCHEDULE_TRIENNIALLY   => 'Triennially'        );    }    const CREATE_ON_ORDER   = 1;    const CREATE_ON_PAYMENT = 2;    const CREATE_ON_ACCEPT  = 3;    public static function getCreateAccountWhen()    {        return array(            self::CREATE_ON_ORDER   => 'When the order is placed',            self::CREATE_ON_PAYMENT => 'When the invoice is paid',            self::CREATE_ON_ACCEPT  => 'When the order is accepted'        );    }}