<?phpnamespace App\AdminBundle\Business\Order;use App\AdminBundle\Business\Base\BaseGridHandler;class GridHandler extends BaseGridHandler{    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $query->select($baseObject)            ->from('AppClientBundle:ProductOrder', $baseObject);        // Build filter here - Consult arrayToSQL($filter, false)        $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }    public function postParseRow(&$r)    {        $r['client']    = $this->helperFormatter->format($r['idClient'], 'mapping', 'client_list');        $r['product']   = $this->helperFormatter->format($r['idProduct'], 'mapping', 'product_list');        $r['timestamp'] = $this->helperFormatter->format($r['timestamp'], 'datetime');        $r['status']    = $this->helperFormatter->format($r['status'], 'mapping', 'order_status');        $r['idInvoice'] = $this->container->get('doctrine')->getRepository('AppClientBundle:ClientInvoice')->findOneById($r['idInvoice'])->getNumber();    }}