<?phpnamespace App\AdminBundle\Business\PermissionGroup;use App\AdminBundle\Business\Base\BaseDeleteHandler;use App\ClientBundle\Entity;class DeleteHandler extends BaseDeleteHandler{    public function execute()    {        $model = $this->getModel();        $em = $this->container->get('doctrine')->getEntityManager();        $group = $em->getRepository('AppClientBundle:PermissionGroup')->findOneBy(['id' => $model['id']]);        $em->remove($group);        $em->flush();    }}