<?phpnamespace App\AdminBundle\Business\Product;use App\AdminBundle\Business;use App\ClientBundle\Entity;class ImportProcess{    public $container;    public function __construct($container)    {        $this->container = $container;    }    public function doImport()    {        $fileName = $this->container->get('request')->query->get('fileName', false);        $tempFolder = $this->container->getParameter('temp_upload_dir');        if (!$fileName || !file_exists($tempFolder . '/' . $fileName)) {            return array('success' => '0', 'failed' => 'All');        }        $noSuccess = 0;        $noFailed = 0;        $r = Utils::parseCsv($this->container, $tempFolder . '/' . $fileName);        if (!empty($r['errors'])) {            return array('success' => '0', 'failed' => 'All');        }        $em = $this->container->get('doctrine')->getEntityManager();        $helperCommon = $this->container->get('app_admin.helper.common');        foreach ($r['result'] as $row) {            $product = new Entity\Product();            if ($row['message'] == 'OK') {                $helperCommon->copyArrayToEntity($row, $product);                $em->persist($product);                $noSuccess++;            } else {                $noFailed++;            }        }        $em->flush();        return array(            'success' => $noSuccess,            'failed'  => $noFailed        );    }}