<?phpnamespace App\AdminBundle\Business\Product;class Utils{    public static function parseCsv($container, $fileName)    {        $validator = $container->get('app_admin.business.product.import_validator');        $validator->initialize();        $keywords = array('name', 'stock');        $csvParser = $container->get('app_admin.helper.csv_parser');        $errors = array();        $array = $csvParser->parseFile($fileName, $keywords, $validator, $errors);        return array(            'result' => $array,            'errors' => $errors        );    }}