<?phpnamespace App\AdminBundle\Business\ProductGroup;use App\AdminBundle\Business\Base\BaseGridHandler;class GridHandler extends BaseGridHandler{    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $query->select($baseObject)            ->from('AppClientBundle:ProductGroup', $baseObject);        // Build filter here - Consult arrayToSQL($filter, false)        $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }    public function postBuilderQuery($query)    {        $query->orderBy('p.id', 'DESC');    }    public function postParseRow(&$r)    {        $r['orderLink'] = $this->container->get('router')->generate('web_client_order') . '?gid=' . $r['id'];    }}