<?phpnamespace App\AdminBundle\Business\Project;class Constants{    // Status    const STATUS_RECEIVED = 1;    const STATUS_INPROGRESS = 2;    const STATUS_ON_HOLD = 3;    const STATUS_COMPLETED = 4;    public static function getProjectStatus()    {        return array(            self::STATUS_RECEIVED   => 'Received',            self::STATUS_INPROGRESS => 'In progress',            self::STATUS_ON_HOLD    => 'On hold',            self::STATUS_COMPLETED  => 'Completed',        );    }    // Project types    const TYPE_CONSULTANCY = 1;    const TYPE_HOSTING = 2;    const TYPE_CODING = 3;    const TYPE_DESIGN = 4;    const TYPE_OTHER = 5;    public static function getProjectTypes()    {        return array(            self::TYPE_CONSULTANCY => 'Consultancy',            self::TYPE_HOSTING     => 'Hosting',            self::TYPE_CODING      => 'Coding',            self::TYPE_DESIGN      => 'Design',            self::TYPE_OTHER       => 'Other'        );    }}