<?phpnamespace App\AdminBundle\Business\Project;use App\AdminBundle\Business\Base\BaseGridHandler;class GridHandler extends BaseGridHandler{    private function filter($query, $baseObject, $filter)    {        $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }    function buildTotalMatchedQuery($query, $baseObject, $filter)    {        $query->select('COUNT(p)')            ->from('AppClientBundle:ClientProject', $baseObject);        $this->filter($query, $baseObject, $filter);    }    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $query->select($baseObject . ', COUNT(pt.start) - COUNT(pt.stop) timer')            ->from('AppClientBundle:ClientProject', $baseObject)            ->leftJoin('AppClientBundle:ClientProjectTracking', 'pt', 'WITH', 'p.id = pt.idProject')            ->groupBy($baseObject . '.id');        $this->filter($query, $baseObject, $filter);    }    public function parseRow($row)    {        $arr = $this->helperCommon->copyEntityToArray($row[0]);        unset($row[0]);        return array_merge($arr, $row);    }    public function postParseRow(&$r)    {        $r['dueDate'] = $r['dueDate'] != null ? $r['dueDate']->format('m/d/Y') : ''; // Fix me        $r['status'] = $this->helperMapping->getMappingTitle('project_status', $r['status']);        $r['idClient'] = $this->helperMapping->getMappingTitle('client_list', $r['idClient']);    }}