<?phpnamespace App\AdminBundle\Business\ProjectAttachment;use App\AdminBundle\Business\Base\BaseGridHandler;class GridHandler extends BaseGridHandler{    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $idProject = $this->container->get('request')->query->get('id', 0);        $query->select($baseObject)            ->from('AppClientBundle:ClientProjectAttachment', $baseObject)            ->andWhere($baseObject . '.idProject = :idProject')            ->setParameter('idProject', $idProject);        // Build filter here - Consult arrayToSQL($filter, false)        $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }    public function postParseRow(&$r)    {        $r['timestamp'] = $this->helperFormatter->format($r['timestamp'], 'datetime');        $url = $this->container->get('router')->generate('app_admin_project_attachment_download');        $attachments = $this->container->get('app_admin.helper.doctrine')->loadList('AppClientBundle:ClientProjectAttachmentFile', 'idProjectAttachment', 'id', $r['id']);        $r['attachments'] = array();        foreach ($attachments as $file) {            $r['attachments'][] = '<a target="_blank" href="' . $url . '?id=' . $file . '">' . $file . '</a>';        }        $r['attachments'] = implode('&nbsp;&nbsp;', $r['attachments']);    }}