<?phpnamespace App\AdminBundle\Business\Recurring;use App\AdminBundle\Business\Base\BaseDeleteHandler;use App\ClientBundle\Entity;class DeleteHandler extends BaseDeleteHandler{    public function execute()    {        $model = $this->getModel();        // Cascade through invoice items        $em = $this->container->get('doctrine')->getEntityManager();        $items = $em->getRepository('AppClientBundle:ClientRecurringItem')->findByIdRecurring($model['id']);        foreach($items as $item) $em->remove($item);        $em->flush();        // Delete the invoice        $this->helperDoctrine->deleteOneById('AppClientBundle:ClientRecurring', $model['id']);    }}