<?phpnamespace App\AdminBundle\Business\Staff;use Symfony\Component\Validator\ExecutionContext;class EditModel{    public $firstname;    public $lastname;    public $email;    public $password;    public $hourly;    public $plainPassword;    public $defaultHourlyRate;    public $permissionGroup;    public function isValid(ExecutionContext $context)    {        $helperDoctrine = $this->container->get('app_admin.helper.doctrine');        if ($helperDoctrine->isNotExistControl('AppUserBundle:User', 'email', $this->email, $this->entityId) === false) {            $context->addViolationAtSubPath('email', "Email '" . $this->email . "' already exists");        }    }}