<?phpnamespace App\AdminBundle\Business\Tax;class Utils{    public static function calculateTaxByClient($container, $client, $tax)    {        if($client == "" || $tax == "") return 0;        $clientCountry = $container->get('doctrine')->getRepository('AppUserBundle:User')->findOneById($client)->getIdCountry();        $em = $container->get('doctrine')->getEntityManager();        $query = $em->createQueryBuilder();        $query->select('p.value, p.countries')            ->from('AppClientBundle:Tax', 'p')            ->andWhere('p.id = :tid')            ->setParameter('tid', $tax)        ;        $result = $query->getQuery()->getSingleResult();        if(in_array($clientCountry, $result['countries'])) return $result['value'];        return 0;    }}