<?phpnamespace App\AdminBundle\Controller;use Symfony\Component\HttpFoundation\Response;use App\AdminBundle\Business;class SearchController extends BaseController{    public function searchAction()    {        $data = array();        $this->get('app_admin.helper.billr_application')->buildViewHeader($data, array(            'headerMenuSelected' => 'dashboard'        ));        $s = $this->get('request')->query->get('s', false);        $data['s'] = $s;        if (empty($s)) {            return $this->redirect($this->generateUrl('app_admin_dashboard_list'));        }        $data['result'] = Business\Search\Utils::search($this, $s);        return $this->render('AppAdminBundle:Search:result.html.twig', $data);    }}