<?phpnamespace App\AdminBundle\Form\Type;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\FormBuilder;use Symfony\Component\Form\FormInterface;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\Form\FormView;use Symfony\Component\Form\FormEvent;use Symfony\Component\Form\FormEvents;use App\AdminBundle\Form\Transformer;class FileAttachment extends AbstractType{    public $container;    public function __construct($container)    {        $this->container = $container;    }    public function getDefaultOptions(array $options)    {        return array('compound' => false);    }    public function buildForm(FormBuilderInterface $builder, array $options)    {    }    public function finishView(FormView $view, FormInterface $form, array $options)    {        $view->vars['full_name'] = $view->vars['full_name'] . '[]';        $view->vars['multipart'] = true;        $view->vars['attr']['type'] = 'file';    }    public function getParent()    {        return 'field';    }    public function getName()    {        return 'file_attachment';    }}