<?phpnamespace App\AdminBundle\Helper;use Symfony\Component\DependencyInjection\ContainerInterface;class Form{    /** @var ContainerInterface */    protected $container;    public function __construct(ContainerInterface $container)    {        $this->container = $container;    }    public function buildCreateFormView($formHandler, $options = array())    {        return array(            'id'          => isset($options['id']) ? $options['id'] : 'CreateFormId',            'title'       => isset($options['title']) ? $options['title'] : 'Create Form',            'view'        => $formHandler->getForm()->createView(),            'submitLabel' => isset($options['submit_label']) ? $options['submit_label'] : "Submit",            'messages'    => $formHandler->getMessages(),            'errors'      => $formHandler->getErrors(),            'action'      => $this->container->get('router')->generate($options['route'], $this->container->get('request')->query->all())        );    }    public function buildEditFormView($formHandler, $options = array())    {        //routeOption = isset($options['route_option']) ? $options['route_option'] : array('id' => $this->container->get('request')->query->get('id', 0));        $routeOption = $this->container->get('request')->query->all();        return array(            'id'          => isset($options['id']) ? $options['id'] : 'CreateFormId',            'title'       => isset($options['title']) ? $options['title'] : 'Create Form',            'view'        => $formHandler->getForm()->createView(),            'submitLabel' => isset($options['submit_label']) ? $options['submit_label'] : "Submit",            'messages'    => $formHandler->getMessages(),            'errors'      => $formHandler->getErrors(),            'action'      => $this->container->get('router')->generate($options['route'], $routeOption)        );    }}