<?phpnamespace App\ClientBundle\Business\Client;use Symfony\Component\Validator\ExecutionContext;class CreateModel{    public $container;    public $firstname;    public $lastname;    public $company;    public $address1;    public $address2;    public $city;    public $state;    public $postcode;    public $idCountry;    public $phone;    public $email;    public $password;    public $status;    public function isValid(ExecutionContext $context)    {        $helperDoctrine = $this->container->get('app_admin.helper.doctrine');        if ($helperDoctrine->isNotExist('AppUserBundle:User', 'email', $this->email) === false) {            $context->addViolationAtSubPath('email', "Email '" . $this->email . "' is already existed");        }    }}