<?phpnamespace App\ClientBundle\Business\Contact;use App\ClientBundle\Business\Base\BaseGridHandler;class GridHandler extends BaseGridHandler{    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $query->select($baseObject)            ->from('AppClientBundle:ClientContact', $baseObject)            ->andWhere($baseObject . '.idClient = :idClient')            ->setParameter('idClient', $this->getUserId());        // Build filter here - Consult arrayToSQL($filter, false)        $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }}