<?phpnamespace App\ClientBundle\Business\Order;class Constants{    const SCHEDULE_MONTHLY = 1;    const SCHEDULE_QUARTLY = 2;    const SCHEDULE_SEMI_ANNUALLY = 3;    const SCHEDULE_ANNUALLY = 4;    const SCHEDULE_BIENNIALY = 5;    const SCHEDULE_TRIENNIALY = 6;    public static function getOrderPaymentTerms()    {        return array(            self::SCHEDULE_MONTHLY       => 'Monthly',            self::SCHEDULE_QUARTLY       => 'Quarterly',            self::SCHEDULE_SEMI_ANNUALLY => 'Semi-annually',            self::SCHEDULE_ANNUALLY      => 'Annually',            self::SCHEDULE_BIENNIALY     => 'Biennialy',            self::SCHEDULE_TRIENNIALY    => 'Triennialy'        );    }}