<?phpnamespace App\ClientBundle\Business\Project;use App\ClientBundle\Business\Base\BaseGridHandler;class GridHandler extends BaseGridHandler{    private function filter($query, $baseObject, $filter)    {        $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $query->select($baseObject)            ->from('AppClientBundle:ClientProject', $baseObject)            ->andWhere($baseObject . '.idClient = :idClient')            ->setParameter('idClient', $this->getUserId());        $this->filter($query, $baseObject, $filter);    }    public function postParseRow(&$r)    {        $r['dueDate'] = $this->helperFormatter->format($r['dueDate'], 'date');        $r['status'] = $this->helperMapping->getMappingTitle('project_status', $r['status']);        $r['idType'] = $this->helperMapping->getMappingTitle('project_type', $r['idType']);        $r['idClient'] = $this->helperMapping->getMappingTitle('client_list', $r['idClient']);        $r['budget'] = $this->helperFormatter->format($r['budget'], 'money');    }}