<?phpnamespace App\ClientBundle\Business\Project;use App\ClientBundle\Business\Base\BaseGridHandler;class TrackingGridHandler extends BaseGridHandler{    function buildBaseQuery($query, $baseObject = 'p', $filter)    {        $idProject = $this->container->get('request')->query->get('id', 0);        $query->select($baseObject)            ->from('AppClientBundle:ClientProjectTracking', $baseObject)            ->andWhere($baseObject . '.idProject = :idProject')            ->setParameter('idProject', $idProject);        // Build filter here - Consult arrayToSQL($filter, false)        $this->container->get('app_admin.helper.kendo_grid')->filter($query, $baseObject, $filter);    }    public function postParseRow(&$r)    {        $r['start'] = $this->helperFormatter->format($r['start'], 'datetime');        $r['stop'] = $this->helperFormatter->format($r['stop'], 'datetime');        $r['staff'] = $this->helperFormatter->format($r['staff'], 'mapping', 'staff_list');        $r['hourly'] = $this->helperFormatter->format($r['hourly'], 'money');    }}